package com.ty.winchat.ui;import com.webdesign688.shot360.R;import android.app.Activity;import android.content.Intent;import android.os.Bundle;import android.view.View;import android.view.View.OnClickListener;import android.widget.Button;import android.widget.EditText;public class Main1 extends Activity {	private Button set_icon_btn;	private EditText mEtmain1;	private Button set_icon_btn1;	private Button set_icon_btn2;	@Override	protected void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		setContentView(R.layout.main1);		set_icon_btn =(Button)this.findViewById(R.id.set_icon_btn);		set_icon_btn.setOnClickListener(listener);		set_icon_btn1 =(Button)this.findViewById(R.id.set_icon_btn1);		set_icon_btn1.setOnClickListener(listener);		set_icon_btn2 =(Button)this.findViewById(R.id.set_icon_btn2);		set_icon_btn2.setOnClickListener(listener);				mEtmain1 =(EditText)this.findViewById(R.id.mEtmain1);			}		OnClickListener listener =new OnClickListener() {				@Override		public void onClick(View v) {			switch (v.getId()) {			case R.id.set_icon_btn:				Intent intent =new Intent(getApplicationContext(), VideoChat.class);				intent.putExtra("IP", mEtmain1.getText().toString());				startActivity(intent);				break;			case R.id.set_icon_btn1:				Intent intent1 =new Intent(getApplicationContext(), VideoChatClient.class);				intent1.putExtra("IP", mEtmain1.getText().toString());				startActivity(intent1);				break;			case R.id.set_icon_btn2:				Intent intent2 =new Intent(getApplicationContext(), VideoChatService.class);				intent2.putExtra("IP", mEtmain1.getText().toString());				startActivity(intent2);				break;						default:				break;			}		}	};}